/*
 * Public API Surface of cache
 */

export { CacheConfig, Cacheable, CacheEvict, CachePut } from './lib/CacheDecorator';
export { CacheManager } from './lib/cache-manager/CacheManager';
export { SimpleCacheManager } from './lib/cache-manager/SimpleCacheManager';
export { getCacheManager, EnableCaching } from './lib/CacheInstance';
export { Cache } from './lib/CacheModel';
export { NoOpCache } from './lib/impl/no-op-cache';
export { MemoryCache } from './lib/impl/MemoryCache';
export { DefaultKeyGenerator } from './lib/key-generator/KeyGenerator';
export * from './lib/utils'

import 'reflect-metadata/Reflect';
import { getCacheManager, getKeyGenerator } from './CacheInstance';
import { Cache } from './CacheModel';
import { getParamNames, isPromise } from './utils';

export const METADATA_KEY_CACHE_DEFAULTS = '_cache_defaults';
export const METADATA_KEY_CACHE_KEYS = '_cache_keys';
export const METADATA_KEY_CACHE_VALUE = '_cache_value';


export interface CacheParams {
  cacheName?: string;
  key?: string;
}

export interface CacheEvictParams extends CacheParams {
  afterInvocation?: boolean;
  allEntries?: boolean;
}

export interface CacheableParams extends CacheParams {
  ttl?: number // second
}


export function CacheConfig(cacheName: string): ClassDecorator {
  return (target: any): void => {
    Reflect.defineMetadata(METADATA_KEY_CACHE_DEFAULTS, cacheName, target);
  };
}

function CacheAction<T extends CacheParams>(action: (originTarget, cache, cacheKey, originalMethod, args) => {}, params?: T): MethodDecorator {
  params = getDefaultParams(params);
  return (target: any, propertyKey: string | symbol, descriptor: TypedPropertyDescriptor<any>): TypedPropertyDescriptor<any> => {
    const originalMethod = descriptor.value;
    descriptor.value = (...args: any[]) => {
      const cache: Cache = getCache(target, params);
      const argsObj: any = getArgsObject(originalMethod, args)
      const cacheKey: string = getKeyGenerator().generate(target, propertyKey, argsObj, params.key);
      return action(this, cache, cacheKey, originalMethod, args);
    };
    return descriptor;
  };
}

export function Cacheable(params?: CacheableParams): MethodDecorator {
  params = getDefaultParams(params);
  return (target: any, propertyKey: string | symbol, descriptor: TypedPropertyDescriptor<any>): TypedPropertyDescriptor<any> => {
    const originalMethod = descriptor.value;
    descriptor.value = async function (...args: any[]) {
      const cache: Cache = getCache(target, params);
      const argsObj: any = getArgsObject(originalMethod, args)
      const cacheKey: string = getKeyGenerator().generate(target, propertyKey, argsObj, params.key);
      const oldVal: any = cache.get(cacheKey);
      if (!isPromise(oldVal) && oldVal) {
        return oldVal;
      }
      if (isPromise(oldVal)) {
        const data = await oldVal
        if (data !== undefined) {
          return oldVal;
        }
      }
      const result = originalMethod.apply(this, args);
      if (isPromise(result)) {
        result.then((data) => {
          cache.put(cacheKey, data)
          return data;
        })
      } else if (result !== undefined) {
        cache.put(cacheKey, result)
      }
      return result;
    };
    return descriptor;
  };

}


export function CachePut(params?: CacheableParams): MethodDecorator {
  params = getDefaultParams(params);
  return (target: any, propertyKey: string | symbol, descriptor: TypedPropertyDescriptor<any>): TypedPropertyDescriptor<any> => {
    const originalMethod = descriptor.value;
    descriptor.value = function (...args: any[]) {
      const cache: Cache = getCache(target, params);
      const argsObj: any = getArgsObject(originalMethod, args)
      const cacheKey: string = getKeyGenerator().generate(target, propertyKey, argsObj, params.key);
      const result: any = originalMethod.apply(this, args);
      if (isPromise(result)) {
        result.then((data) => {
          cache.put(cacheKey, data)
          return data
        })
      } else if (result !== undefined) {
        cache.put(cacheKey, result)
      }
      return result;
    };
    return descriptor;
  };


}

export function CacheEvict(params?: CacheEvictParams): MethodDecorator {
  params = getDefaultParams(params);
  return (target: any, propertyKey: string | symbol, descriptor: TypedPropertyDescriptor<any>): TypedPropertyDescriptor<any> => {
    const originalMethod = descriptor.value;
    descriptor.value = async function (...args: any[]) {
      const cache: Cache = getCache(target, params);
      const argsObj: any = getArgsObject(originalMethod, args)
      const cacheKey: string = getKeyGenerator().generate(target, propertyKey, argsObj, params.key);
      if (!params.afterInvocation) {
        if (params.allEntries) {
          await cache.clear()
        } else {
          await cache.evict(cacheKey);
        }
      }
      const result: any = originalMethod.apply(this, args);
      if (params.afterInvocation) {
        if (params.allEntries) {
          await cache.clear()
        } else {
          await cache.evict(cacheKey);
        }
      }
      return result;
    };
    return descriptor;
  };

}


function getCache(target: any, params: CacheParams): Cache {
  let cacheName: string
  if (!params || !params.cacheName) {
    cacheName = Reflect.getMetadata(METADATA_KEY_CACHE_DEFAULTS, target.constructor) || 'default';
  }
  const cache: Cache = getCacheManager().getCache(cacheName);
  if (!cache) {
    throw new Error(`Cache '${cacheName}' not found for ${target.constructor.name}`);
  }
  return cache;
}

function getDefaultParams<T>(cacheParams: CacheParams): T {
  // @ts-ignore
  return Object.assign({
    afterInvocation: true
  }, cacheParams || {});
}


function getArgsObject(func: () => {}, args: any[]) {
  const paramName = getParamNames(func);
  if (paramName.length === 0) {
    return {}
  }
  const result = {}
  for (let i = 0; i < paramName.length; i++) {
    result[paramName[i]] = args[i]
  }
  return result;
}

import { CacheManager } from './cache-manager/CacheManager';
import { MemoryCache } from './impl/MemoryCache';
import { Cache, CacheConfig } from './CacheModel';
import { AutoCacheManager } from './cache-manager/AutoCacheManager';
import { DefaultKeyGenerator } from './key-generator/KeyGenerator';
import { LoggingCache } from './impl/LoggingCache';
import { HashKeyGenerator } from './key-generator/HashKeyGenerator';

const CACHE_INSTANCE = {
  manager: undefined,
  keyGenerator: undefined,
  cache: undefined
};


export function createCache(name: string): Cache {
  return new LoggingCache(new CACHE_INSTANCE.cache(name));
  // return new CACHE_INSTANCE.cache(name);
}

export function EnableCaching(config: CacheConfig) {
  CACHE_INSTANCE.keyGenerator = config.keyGenerator || new HashKeyGenerator();
  CACHE_INSTANCE.cache = config.cache || MemoryCache
  const defaultCache: Cache = createCache('default')
  CACHE_INSTANCE.manager = new AutoCacheManager([defaultCache]);
}

export function getKeyGenerator() {
  if (!CACHE_INSTANCE.keyGenerator) {
    throw new Error('No cache key generator found, initCache before')
  }
  return CACHE_INSTANCE.keyGenerator;
}

export function getCacheManager(): CacheManager {
  if (!CACHE_INSTANCE.manager) {
    throw new Error('No cache found, `initCacheManager` before');
  }
  return CACHE_INSTANCE.manager;
}

import { Cache } from '../CacheModel';

export class MemoryCache implements Cache {

  readonly name: string;
  private cache: Map<string, any> = new Map<string, any>();

  constructor(name: string) {
    this.name = name;
  }
  keys(): string[] | Promise<string[]> {
    return Array.from(this.cache.keys());
  }

  clear(): void {
    this.cache.clear();
  }

  evict(key: string): void {
    this.cache.delete(key);
  }

  get<T>(key: string): T {
    return this.cache.get(key);
  }

  put<T>(key: string, value: T): void {
    this.cache.set(key, value);
  }

}

import { CacheManager } from './CacheManager';
import { Cache } from '../CacheModel';
import { SimpleCacheManager } from './SimpleCacheManager';
import { createCache } from '../CacheInstance';

export class AutoCacheManager implements CacheManager {
    private readonly manager: CacheManager;

    constructor(caches?: Cache[]) {
        this.manager = new SimpleCacheManager(caches);
    }
    addCache(cache: Cache): void {
        this.manager.addCache(cache);
    }

    getCache(name: string): Cache {
        let cache: Cache = this.manager.getCache(name)
        if (cache == null) {
            cache = createCache(name);
            this.addCache(cache);
        }
        return cache;
    }

    getCacheNames(): string[] {
        return this.manager.getCacheNames();
    }

}

import {CacheManager} from './CacheManager';
import {Cache} from '../CacheModel';

export class SimpleCacheManager implements CacheManager {
  private cacheMap: Map<string, Cache>;

  constructor(caches?: Cache[]) {
    this.setCaches(caches);
  }

  getCacheNames(): string[] {
    return Array.from(this.cacheMap.keys());
  }

  getCache(name: string): Cache {
    return this.cacheMap.get(name) || null;
  }

  addCache(cache: Cache): void {
    if (!cache) {
      throw new Error('Cache is undefined');
    }
    this.cacheMap.set(cache.name, cache);
  }

  setCaches(caches: Cache[]): void {
    this.cacheMap = new Map<string, Cache>();
    if (caches) {
      caches.forEach(cache => this.addCache(cache));
    }
  }
}

import { Cache } from '../CacheModel';

export class LoggingCache implements Cache {
    name: string;
    protected requests = 0;
    protected hits = 0;
    protected logEnable = process.env.DEBUG_ENABLE === 'true';

    private readonly cache: Cache;

    constructor(cache: Cache) {
        this.cache = cache;
        this.name = cache.name;
    }
    keys(): string[] | Promise<string[]> {
        return this.cache.keys();
    }



    get<T>(key: string): T | Promise<T> {
        this.requests++;
        const value: T | Promise<T> = this.cache.get(key)
        if (value != null) {
            this.hits++;
        }
        if (this.logEnable) {
            console.log(`get data from cache, key: ${key}, value: ${JSON.stringify(value)}`)
            console.log("Cache Hit Ratio [" + this.name + "]: " + this.getHitRatio());
        }
        return value;
    }
    put<T>(key: string, value: T): void {
        if (this.logEnable) {
            console.log(`caching data in [${this.name}], key: ${key}, value: ${JSON.stringify(value)}`)
        }
        this.cache.put(key, value)
    }
    evict(key: string): void {
        if (this.logEnable) {
            console.log(`evict key [${this.name}]: ${key}`)
        }
        this.cache.evict(key)
    }
    clear(): void {
        if (this.logEnable) {
            console.log(`clear cache [${this.name}]`)
        }
        this.cache.clear()
    }

    private getHitRatio() {
        return this.hits / this.requests;
    }

}

import { DefaultKeyGenerator } from './KeyGenerator'
import { hash } from '../utils'

export class HashKeyGenerator extends DefaultKeyGenerator {
    generate(target: any, propertyKey: string | symbol, args: any, key?: string): string {
        if (!key) {
            return propertyKey.toString() +'_'+ hash(JSON.stringify(args))
        }
        return this.template(key, args)
    }
}

import { hashCode, hash } from '../utils';

export interface KeyGenerator {
    generate(target: any, propertyKey: string | symbol, args: any, key?: string): string;
}

export class DefaultKeyGenerator implements KeyGenerator {

    template(tpl, data) {
        const pattern = /\$\{([^\}\}]+)?\}/g;
        let body = 'return "';
        body += tpl.replace(pattern,  (m, g) =>{
            return '" + this.' + g.trim() + ' + "';
        });
        body += '";';
        return new Function(body.replace(/[\t\n\r]/g, '')).apply(data);
    };


    generate(target: any, propertyKey: string | symbol, args: any, key?: string): string {
        if (!key) {
            return propertyKey.toString() + JSON.stringify(args)
        }
        console.log('args:', JSON.stringify(args))
        return this.template(key, args)
    }

}

import crypto = require('crypto');

export function Measure(threshold): MethodDecorator {
    threshold = threshold || 0
    return (target: any, propertyKey: string | symbol, descriptor: TypedPropertyDescriptor<any>): TypedPropertyDescriptor<any> => {
        const oldValue: any = descriptor.value;
        descriptor.value = async function () {
            const start: number = new Date().getTime();
            const res: any = await oldValue.apply(this, arguments);
            const time: number = new Date().getTime() - start;
            if (time > threshold) {
                console.info(`called function [${name}], take: ${time} ms`);
            }
            return res;
        };
        return descriptor;
    }
}


/**
 * get parameter name for a function
 *
 * @export
 * @param {*} func
 * @returns name list
 */
export function getParamNames(func) {
    const STRIP_COMMENTS = /(\/\/.*$)|(\/\*[\s\S]*?\*\/)|(\s*=[^,\)]*(('(?:\\'|[^'\r\n])*')|("(?:\\"|[^"\r\n])*"))|(\s*=[^,\)]*))/mg;
    const ARGUMENT_NAMES = /([^\s,]+)/g;
    const fnStr = func.toString().replace(STRIP_COMMENTS, '');
    let result = fnStr.slice(fnStr.indexOf('(') + 1, fnStr.indexOf(')')).match(ARGUMENT_NAMES);
    if (result === null)
        result = [];
    return result;
}

export function hashCode(str: string) {
    let hashStr = 0;
    if (str.length === 0) return hashStr;
    for (let i = 0; i < str.length; i++) {
        const char = str.charCodeAt(i);
        // tslint:disable-next-line: no-bitwise
        hashStr = ((hashStr << 5) - hashStr) + char;
        // tslint:disable-next-line: no-bitwise
        hashStr = hashStr & hashStr; // Convert to 32bit integer
    }
    return hashStr;
}

export function hash(str: string) {
    const md5 = crypto.createHash('md5');// :md5,md5hash
    md5.update(str);
    const result = md5.digest('hex');  // d
    return result;
}

/**
 * Tests to see if the object is an ES2015 (ES6) Promise
 * @see {@link https://www.ecma-international.org/ecma-262/6.0/#sec-promise-objects}
 * @param value the object to test
 */
export function isPromise(value: any): value is PromiseLike<any> {
    return !!value && typeof value.subscribe !== 'function' && typeof value.then === 'function';
}

import {Cache} from '../CacheModel';

export class NoOpCache implements Cache {

  readonly name: string;

  constructor(name: string) {
    this.name = name;
  }
  keys(): string[] | Promise<string[]> {
    throw new Error('Method not implemented.');
  }

  // tslint:disable-next-line: no-empty
  clear(): void {
  }

  // tslint:disable-next-line: no-empty
  evict(key: string): void {
  }

  get<T>(key: string): T {
    return undefined;
  }

  // tslint:disable-next-line: no-empty
  put<T>(key: string, value: T): void {
  }
}
